// src/pages/Home.js
import React, { useContext } from "react";
import { UIContext } from "../context/UIContext";

export default function Home() {
  const { fontSize, colorblindMode, simplifiedUI } = useContext(UIContext);

  // Font size classes
  const fontSizeClass = {
    sm: "text-sm",
    md: "text-base",
    lg: "text-lg",
  }[fontSize];

  // Colorblind mode filter
  const colorblindClass = {
    none: "",
    protanopia: "filter hue-rotate-90",
    deuteranopia: "filter hue-rotate-180",
    tritanopia: "filter hue-rotate-270",
  }[colorblindMode];

  return (
    <div className={`space-y-8 ${fontSizeClass} ${colorblindClass} transition-all duration-500`}>
      <h1 className="text-5xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 animate-text">
        Welcome to SH Project
      </h1>

      <p className={`text-gray-700 dark:text-gray-200 ${simplifiedUI ? "text-base" : "text-lg"} transition-all`}>
        Your personal health dashboard and AI assistant. Track hydration, UV exposure,
        steps, mental health, and get advice from our smart AI assistant.
      </p>

      <div
        className={`p-6 rounded-2xl shadow-2xl transition-transform duration-500 transform hover:scale-105 ${
          simplifiedUI
            ? "bg-gray-600 text-white"
            : "bg-gradient-to-r from-indigo-400 to-purple-500 text-white"
        }`}
      >
        <h2 className="text-3xl font-bold mb-2">Get Started</h2>
        <p className="text-white">
          Navigate to the Dashboard to see your personalized metrics and start chatting with your Health AI.
        </p>
      </div>

      <div className={`grid grid-cols-1 md:grid-cols-2 gap-6`}>
        <div className={`p-6 rounded-xl shadow-xl transition-transform duration-500 transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-700 text-white" : "bg-gradient-to-r from-purple-400 to-pink-500 text-white"
        }`}>
          <h3 className="text-2xl font-bold mb-1">Hydration Tracker</h3>
          <p>Monitor daily water intake and stay hydrated with reminders.</p>
        </div>

        <div className={`p-6 rounded-xl shadow-xl transition-transform duration-500 transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-700 text-white" : "bg-gradient-to-r from-indigo-400 to-purple-500 text-white"
        }`}>
          <h3 className="text-2xl font-bold mb-1">Step Counter</h3>
          <p>Track your daily steps and activity levels to reach fitness goals.</p>
        </div>

        <div className={`p-6 rounded-xl shadow-xl transition-transform duration-500 transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-700 text-white" : "bg-gradient-to-r from-pink-400 to-red-500 text-white"
        }`}>
          <h3 className="text-2xl font-bold mb-1">UV Exposure</h3>
          <p>Monitor UV levels and get recommendations to protect your skin.</p>
        </div>

        <div className={`p-6 rounded-xl shadow-xl transition-transform duration-500 transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-700 text-white" : "bg-gradient-to-r from-green-400 to-teal-500 text-white"
        }`}>
          <h3 className="text-2xl font-bold mb-1">Mental Health</h3>
          <p>Track mood, stress levels, and receive mental health tips from AI.</p>
        </div>
      </div>
    </div>
  );
}
