// src/pages/Dashboard.js
import React, { useEffect, useState, useContext } from "react";
import Chatbot from "../components/Chatbot";
import { UIContext } from "../context/UIContext";
import axios from "axios";

export default function Dashboard() {
  const { simplifiedUI } = useContext(UIContext);

  const [hydration, setHydration] = useState(32);
  const [hydrationGoal, setHydrationGoal] = useState(64);
  const [uvIndex, setUvIndex] = useState(null);
  const [mentalHealth, setMentalHealth] = useState("Good");
  const [steps, setSteps] = useState(5200);
  const [zipCode, setZipCode] = useState(""); // user location
  const [airQuality, setAirQuality] = useState(null);
  const [weather, setWeather] = useState(null);
  const [disasters, setDisasters] = useState([]);

  // API keys
  const OPENWEATHER_API_KEY = "d8f74cc22f35aed3b07c8f47c6d7f6d1";
  const AIRNOW_API_KEY = "B11FBD4A-B70D-4288-888D-5B39C19CF64D";

  useEffect(() => {
    if (zipCode.length === 5) {
      // Fetch weather + UV
      axios
        .get(`https://api.openweathermap.org/data/2.5/weather?zip=${zipCode}&units=imperial&appid=${OPENWEATHER_API_KEY}`)
        .then((res) => {
          const data = res.data;
          setWeather(`${data.main.temp}°F, ${data.weather[0].description}`);
          // UV index requires separate endpoint (lat/lon)
          const { lat, lon } = data.coord;
          axios
            .get(`https://api.openweathermap.org/data/2.5/uvi?appid=${OPENWEATHER_API_KEY}&lat=${lat}&lon=${lon}`)
            .then((uvRes) => setUvIndex(uvRes.data.value))
            .catch(() => setUvIndex(null));
        })
        .catch(() => setWeather("Unable to fetch weather"));

      // Fetch air quality
      axios
        .get(`https://www.airnowapi.org/aq/observation/zipCode/current/?format=application/json&zipCode=${zipCode}&distance=25&API_KEY=${AIRNOW_API_KEY}`)
        .then((res) => {
          if (res.data.length) setAirQuality(res.data[0].AQI);
        })
        .catch(() => setAirQuality(null));
    }

    // Fetch recent disasters
    axios
      .get("https://eonet.gsfc.nasa.gov/api/v3/events")
      .then((res) => setDisasters(res.data.events.slice(0, 3)))
      .catch(() => setDisasters([]));
  }, [zipCode]);

  return (
    <div className="space-y-8">
      <h1 className="text-3xl font-extrabold text-gray-900 dark:text-white">Dashboard</h1>

      {/* Location input */}
      <div className="flex gap-4 items-center">
        <input
          type="text"
          placeholder="Enter ZIP code"
          value={zipCode}
          onChange={(e) => setZipCode(e.target.value)}
          className="p-2 rounded-lg border border-gray-300 dark:border-gray-600"
        />
        <span className="text-gray-500 dark:text-gray-300">Enter ZIP to fetch local weather & air quality</span>
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Hydration */}
        <div className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : "bg-gradient-to-r from-indigo-400 to-purple-500 text-white"
        }`}>
          <h2 className="text-lg font-semibold mb-2">Hydration</h2>
          <p className="mb-2 font-medium">{hydration} oz / {hydrationGoal} oz</p>
          <div className="w-full bg-gray-300 dark:bg-gray-600 rounded-full h-4">
            <div className="h-4 rounded-full bg-blue-500" style={{ width: `${(hydration / hydrationGoal) * 100}%` }}></div>
          </div>
        </div>

        {/* UV Index */}
        <div className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : "bg-gradient-to-r from-yellow-300 to-yellow-500 text-gray-900"
        }`}>
          <h2 className="text-lg font-semibold mb-2">UV Index</h2>
          <p className="mb-2 font-medium">{uvIndex !== null ? uvIndex : "Loading..."}</p>
          <div className="w-full bg-yellow-200 rounded-full h-4">
            <div className="h-4 rounded-full bg-yellow-600" style={{ width: `${uvIndex ? (uvIndex / 11) * 100 : 0}%` }}></div>
          </div>
        </div>

        {/* Steps */}
        <div className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : "bg-gradient-to-r from-green-400 to-green-500 text-white"
        }`}>
          <h2 className="text-lg font-semibold mb-2">Steps</h2>
          <p className="mb-2 font-medium">{steps}</p>
        </div>

        {/* Mental Health */}
        <div className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : "bg-gradient-to-r from-pink-400 to-red-500 text-white"
        }`}>
          <h2 className="text-lg font-semibold mb-2">Mental Health</h2>
          <p className="mb-2 font-medium">{mentalHealth}</p>
        </div>

        {/* Air Quality */}
        <div className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : "bg-gradient-to-r from-teal-400 to-blue-500 text-white"
        }`}>
          <h2 className="text-lg font-semibold mb-2">Air Quality Index</h2>
          <p className="mb-2 font-medium">{airQuality !== null ? airQuality : "Enter ZIP code"}</p>
        </div>

        {/* Weather */}
        <div className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
          simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : "bg-gradient-to-r from-indigo-400 to-pink-500 text-white"
        }`}>
          <h2 className="text-lg font-semibold mb-2">Weather</h2>
          <p className="mb-2 font-medium">{weather || "Enter ZIP code"}</p>
        </div>

        {/* Recent Disasters */}
        <div className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 md:col-span-2 lg:col-span-3 ${
          simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : "bg-gradient-to-r from-red-400 to-yellow-400 text-white"
        }`}>
          <h2 className="text-lg font-semibold mb-2">Recent Disasters</h2>
          {disasters.length ? (
            <ul className="list-disc pl-5">
              {disasters.map((d) => (
                <li key={d.id}>{d.title} ({d.categories.map(c => c.title).join(", ")})</li>
              ))}
            </ul>
          ) : (
            <p>Loading recent events...</p>
          )}
        </div>

        {/* Chatbot */}
        <div className="md:col-span-2 lg:col-span-3">
          <Chatbot apiEndpoint="/api/chatbot" />
        </div>
      </div>
    </div>
  );
}
