import React, { useContext } from "react";
import { UIContext } from "../context/UIContext";

const SettingsPage = () => {
  const { theme, toggleTheme, simplifiedUI, toggleSimplifiedUI } = useContext(UIContext);

  return (
    <div className="p-6 max-w-xl mx-auto space-y-6 bg-white dark:bg-gray-800 rounded-xl shadow-lg">
      <h1 className="text-2xl font-bold">Settings</h1>

      <div className="flex items-center justify-between">
        <span className="font-medium">Dark Mode</span>
        <button
          onClick={toggleTheme}
          className="px-4 py-2 bg-indigo-500 text-white rounded-lg hover:bg-indigo-600 transition"
        >
          {theme === "dark" ? "Disable" : "Enable"}
        </button>
      </div>

      <div className="flex items-center justify-between">
        <span className="font-medium">Simplified UI</span>
        <button
          onClick={toggleSimplifiedUI}
          className="px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition"
        >
          {simplifiedUI ? "Disable" : "Enable"}
        </button>
      </div>

      <p className="text-sm text-gray-500 dark:text-gray-300 mt-4">
        Toggle these settings to optimize the interface for your preferences. Dark mode reduces eye strain, and simplified UI reduces cognitive load for easier navigation.
      </p>
    </div>
  );
};

export default SettingsPage;
