import React, { useEffect, useState, useContext } from "react";
import Chatbot from "../components/Chatbot";
import { UIContext } from "../context/UIContext";

export default function Dashboard() {
  const { simplifiedUI } = useContext(UIContext);

  const [hydration, setHydration] = useState(32);
  const [hydrationGoal, setHydrationGoal] = useState(64);
  const [uvIndex, setUvIndex] = useState(7);
  const [mentalHealth, setMentalHealth] = useState("Good");
  const [steps, setSteps] = useState(5200);

  useEffect(() => {
    // TODO: Fetch from backend
  }, []);

  return (
    <div className="space-y-8">
      <h1 className="text-3xl font-extrabold text-gray-900 dark:text-white">
        Dashboard
      </h1>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/** Cards with gradient backgrounds or simplified UI */}
        <Card title="Hydration" value={`${hydration} oz / ${hydrationGoal} oz`} progress={hydration/hydrationGoal} simplifiedUI={simplifiedUI} gradient="from-indigo-400 to-purple-500" color="blue" />
        <Card title="UV Index" value={uvIndex} progress={uvIndex/11} simplifiedUI={simplifiedUI} gradient="from-yellow-300 to-yellow-500" color="yellow"/>
        <Card title="Steps" value={steps} progress={Math.min(steps/10000,1)} simplifiedUI={simplifiedUI} gradient="from-green-400 to-green-500" color="green"/>
        <Card title="Mental Health" value={mentalHealth} simplifiedUI={simplifiedUI} gradient="from-pink-400 to-red-500" color="pink"/>
      </div>

      <div className="mt-6">
        <h2 className="text-2xl font-bold mb-4 text-gray-900 dark:text-white">Health AI Chat</h2>
        <Chatbot apiEndpoint="http://localhost:5000/api/chat" />
      </div>
    </div>
  );
}

const Card = ({ title, value, progress = 0, simplifiedUI, gradient, color }) => (
  <div className={`p-6 rounded-xl shadow-lg hover:shadow-2xl transition ${simplifiedUI ? "bg-gray-100 dark:bg-gray-700" : `bg-gradient-to-r ${gradient} text-white`}`}>
    <h2 className="text-lg font-semibold mb-2">{title}</h2>
    <p className="mb-2 font-medium">{value}</p>
    {progress > 0 && <div className={`w-full bg-${color}-200 dark:bg-gray-600 rounded-full h-4`}>
      <div className={`h-4 rounded-full bg-${color}-600`} style={{width: `${progress*100}%`}}></div>
    </div>}
  </div>
);
