// src/context/UIContext.js
import React, { createContext, useState } from "react";

export const UIContext = createContext();

export const UIProvider = ({ children }) => {
  const [theme, setTheme] = useState("light"); // light/dark
  const [simplifiedUI, setSimplifiedUI] = useState(false);
  const [colorblindMode, setColorblindMode] = useState("none"); // none, protanopia, deuteranopia, tritanopia
  const [fontSize, setFontSize] = useState("md"); // sm, md, lg

  const toggleTheme = () =>
    setTheme(theme === "light" ? "dark" : "light");
  const toggleSimplifiedUI = () => setSimplifiedUI(!simplifiedUI);
  const changeColorblindMode = (mode) => setColorblindMode(mode);
  const changeFontSize = (size) => setFontSize(size);

  return (
    <UIContext.Provider
      value={{
        theme,
        toggleTheme,
        simplifiedUI,
        toggleSimplifiedUI,
        colorblindMode,
        changeColorblindMode,
        fontSize,
        changeFontSize,
      }}
    >
      {children}
    </UIContext.Provider>
  );
};
