// src/pages/Dashboard.js
import React, { useEffect, useState, useContext } from "react";
import Chatbot from "../components/Chatbot";
import { UIContext } from "../context/UIContext";
import axios from "axios";

export default function Dashboard() {
  const { simplifiedUI } = useContext(UIContext);

  const [hydration, setHydration] = useState(0);
  const [hydrationGoal, setHydrationGoal] = useState(64);
  const [uvIndex, setUvIndex] = useState(null);
  const [mentalHealth, setMentalHealth] = useState("Good");
  const [steps, setSteps] = useState(0);
  const [zipCode, setZipCode] = useState(""); // user location
  const [airQuality, setAirQuality] = useState(null);
  const [weather, setWeather] = useState(null);
  const [disasters, setDisasters] = useState([]);

  // Fetch all metrics from backend
  const fetchMetrics = async () => {
    try {
      const res = await axios.get(`/api/metrics`, { params: { zip: zipCode } });
      const data = res.data;

      setHydration(data.hydration.value);
      setHydrationGoal(data.hydration.goal);
      setUvIndex(data.uv);
      setMentalHealth(data.mental_health);
      setSteps(data.steps);
      setAirQuality(data.air_quality);
      setWeather(`${data.weather.temp}°F, ${data.weather.description}`);
      setDisasters(data.disasters.slice(0, 3));
    } catch (err) {
      console.error("Error fetching metrics:", err);
    }
  };

  // Update a specific metric
  const updateMetric = async (key, value) => {
    try {
      const res = await axios.post(`/api/metrics/update`, { [key]: value });
      const updated = res.data.metrics;

      // Update state immediately
      setHydration(updated.hydration);
      setHydrationGoal(updated.hydration_goal);
      setSteps(updated.steps);
      setMentalHealth(updated.mental_health);
      setUvIndex(updated.uv_index);
    } catch (err) {
      console.error("Error updating metric:", err);
    }
  };

  // Reset all metrics
  const resetMetrics = async () => {
    try {
      const res = await axios.post(`/api/metrics/reset`);
      const reset = res.data.metrics;

      setHydration(reset.hydration);
      setHydrationGoal(reset.hydration_goal);
      setSteps(reset.steps);
      setMentalHealth(reset.mental_health);
      setUvIndex(reset.uv_index);
      setAirQuality(reset.air_quality);
      setWeather(reset.weather);
      setDisasters(reset.disasters || []);
    } catch (err) {
      console.error("Error resetting metrics:", err);
    }
  };

  // Refetch metrics whenever ZIP code changes
  useEffect(() => {
    if (zipCode.length === 5) fetchMetrics();
  }, [zipCode]);

  return (
    <div className="space-y-8">
      <h1 className="text-3xl font-extrabold text-gray-900 dark:text-white">
        Dashboard
      </h1>

      {/* Location input */}
      <div className="flex gap-4 items-center">
        <input
          type="text"
          placeholder="Enter ZIP code"
          value={zipCode}
          onChange={(e) => setZipCode(e.target.value)}
          className="p-2 rounded-lg border border-gray-300 dark:border-gray-600"
        />
        <span className="text-gray-500 dark:text-gray-300">
          Enter ZIP to fetch local weather & air quality
        </span>
      </div>

      {/* Reset Button */}
      <div>
        <button
          onClick={resetMetrics}
          className="px-4 py-2 bg-red-600 text-white rounded-lg shadow hover:bg-red-700 transition"
        >
          Reset Metrics
        </button>
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Hydration */}
        <div
          className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
            simplifiedUI
              ? "bg-gray-100 dark:bg-gray-700"
              : "bg-gradient-to-r from-indigo-400 to-purple-500 text-white"
          }`}
        >
          <h2 className="text-lg font-semibold mb-2">Hydration</h2>
          <p className="mb-2 font-medium">
            {hydration} oz / {hydrationGoal} oz
          </p>
          <div className="w-full bg-gray-300 dark:bg-gray-600 rounded-full h-4 mb-3">
            <div
              className="h-4 rounded-full bg-blue-500"
              style={{ width: `${(hydration / hydrationGoal) * 100}%` }}
            ></div>
          </div>
          <div className="flex gap-2">
            <input
              type="number"
              value={hydration}
              onChange={(e) => setHydration(Number(e.target.value))}
              className="w-20 p-1 rounded text-black"
            />
            <button
              onClick={() => updateMetric("hydration", hydration)}
              className="px-3 py-1 bg-blue-600 text-white rounded-lg shadow"
            >
              Update
            </button>
          </div>
        </div>

        {/* UV Index */}
        <div
          className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
            simplifiedUI
              ? "bg-gray-100 dark:bg-gray-700"
              : "bg-gradient-to-r from-yellow-300 to-yellow-500 text-gray-900"
          }`}
        >
          <h2 className="text-lg font-semibold mb-2">UV Index</h2>
          <p className="mb-2 font-medium">
            {uvIndex !== null ? uvIndex : "Loading..."}
          </p>
          <div className="w-full bg-yellow-200 rounded-full h-4">
            <div
              className="h-4 rounded-full bg-yellow-600"
              style={{ width: `${uvIndex ? (uvIndex / 11) * 100 : 0}%` }}
            ></div>
          </div>
        </div>

        {/* Steps */}
        <div
          className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
            simplifiedUI
              ? "bg-gray-100 dark:bg-gray-700"
              : "bg-gradient-to-r from-green-400 to-green-500 text-white"
          }`}
        >
          <h2 className="text-lg font-semibold mb-2">Steps</h2>
          <p className="mb-2 font-medium">{steps}</p>
          <div className="flex gap-2">
            <input
              type="number"
              value={steps}
              onChange={(e) => setSteps(Number(e.target.value))}
              className="w-24 p-1 rounded text-black"
            />
            <button
              onClick={() => updateMetric("steps", steps)}
              className="px-3 py-1 bg-green-600 text-white rounded-lg shadow"
            >
              Update
            </button>
          </div>
        </div>

        {/* Mental Health */}
        <div
          className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
            simplifiedUI
              ? "bg-gray-100 dark:bg-gray-700"
              : "bg-gradient-to-r from-pink-400 to-red-500 text-white"
          }`}
        >
          <h2 className="text-lg font-semibold mb-2">Mental Health</h2>
          <p className="mb-2 font-medium">{mentalHealth}</p>
          <select
            value={mentalHealth}
            onChange={(e) => {
              setMentalHealth(e.target.value);
              updateMetric("mental_health", e.target.value);
            }}
            className="p-2 rounded text-black"
          >
            <option>Good</option>
            <option>Okay</option>
            <option>Stressed</option>
            <option>Bad</option>
          </select>
        </div>

        {/* Air Quality */}
        <div
          className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
            simplifiedUI
              ? "bg-gray-100 dark:bg-gray-700"
              : "bg-gradient-to-r from-teal-400 to-blue-500 text-white"
          }`}
        >
          <h2 className="text-lg font-semibold mb-2">Air Quality Index</h2>
          <p className="mb-2 font-medium">
            {airQuality !== null ? airQuality : "Enter ZIP code"}
          </p>
        </div>

        {/* Weather */}
        <div
          className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 ${
            simplifiedUI
              ? "bg-gray-100 dark:bg-gray-700"
              : "bg-gradient-to-r from-indigo-400 to-pink-500 text-white"
          }`}
        >
          <h2 className="text-lg font-semibold mb-2">Weather</h2>
          <p className="mb-2 font-medium">{weather || "Enter ZIP code"}</p>
        </div>

        {/* Recent Disasters */}
        <div
          className={`p-6 rounded-xl shadow-2xl transition transform hover:scale-105 md:col-span-2 lg:col-span-3 ${
            simplifiedUI
              ? "bg-gray-100 dark:bg-gray-700"
              : "bg-gradient-to-r from-red-400 to-yellow-400 text-white"
          }`}
        >
          <h2 className="text-lg font-semibold mb-2">Recent Disasters</h2>
          {disasters.length ? (
            <ul className="list-disc pl-5">
              {disasters.map((d) => (
                <li key={d.id}>
                  {d.title} ({d.categories.map((c) => c.title).join(", ")})
                </li>
              ))}
            </ul>
          ) : (
            <p>Loading recent events...</p>
          )}
        </div>

        {/* Chatbot */}
        <div className="md:col-span-2 lg:col-span-3">
          <Chatbot apiEndpoint="/chatbot" />
        </div>
      </div>
    </div>
  );
}
