// src/pages/Dashboard.js
import React, { useEffect, useState, useContext } from "react";
import Chatbot from "../components/Chatbot";
import { UIContext } from "../context/UIContext";
import axios from "axios";

export default function Dashboard() {
  const { simplifiedUI } = useContext(UIContext);

  const [hydration, setHydration] = useState(0);
  const [hydrationGoal, setHydrationGoal] = useState(64);
  const [uvIndex, setUvIndex] = useState(null);
  const [mentalHealth, setMentalHealth] = useState("Good");
  const [steps, setSteps] = useState(0);
  const [zipCode, setZipCode] = useState("");
  const [airQuality, setAirQuality] = useState(null);
  const [weather, setWeather] = useState(null);
  const [disasters, setDisasters] = useState([]);

  const fetchMetrics = async () => {
    if (zipCode.length !== 5) return;

    try {
      const res = await axios.get(`http://localhost:5000/api/metrics?zip=${zipCode}`);
      const data = res.data;

      setHydration(data.hydration.value);
      setHydrationGoal(data.hydration.goal);
      setUvIndex(data.uv_index);
      setMentalHealth(data.mental_health);
      setSteps(data.steps);
      setAirQuality(data.air_quality);
      setWeather(`${data.weather.temp}°F, ${data.weather.description}`);
      setDisasters(data.disasters.slice(0, 3));
    } catch (err) {
      console.error("Error fetching metrics:", err);
    }
  };

  useEffect(() => {
    fetchMetrics();
  }, [zipCode]);

  const updateMetric = async (key, value) => {
    try {
      const res = await axios.post(`http://localhost:5000/api/metrics/update`, { [key]: value });
      const updated = res.data.metrics;

      setHydration(updated.hydration);
      setHydrationGoal(updated.hydration_goal);
      setSteps(updated.steps);
      setMentalHealth(updated.mental_health);
    } catch (err) {
      console.error("Error updating metric:", err);
    }
  };

  const resetMetrics = async () => {
    try {
      const res = await axios.post(`http://localhost:5000/api/metrics/reset`);
      const reset = res.data.metrics;

      setHydration(reset.hydration);
      setHydrationGoal(reset.hydration_goal);
      setSteps(reset.steps);
      setMentalHealth(reset.mental_health);
      setUvIndex(null);
      setAirQuality(null);
      setWeather(null);
      setDisasters([]);
    } catch (err) {
      console.error("Error resetting metrics:", err);
    }
  };

  return (
    <div className="space-y-8">
      <h1 className="text-3xl font-extrabold">Dashboard</h1>

      <div className="flex gap-4 items-center">
        <input
          type="text"
          placeholder="Enter ZIP code"
          value={zipCode}
          onChange={(e) => setZipCode(e.target.value)}
          className="p-2 rounded-lg border"
        />
        <span>Enter ZIP to fetch local weather & air quality</span>
      </div>

      <div>
        <button onClick={resetMetrics} className="px-4 py-2 bg-red-600 text-white rounded-lg">
          Reset Metrics
        </button>
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Hydration */}
        <div>
          <h2>Hydration</h2>
          <p>{hydration} oz / {hydrationGoal} oz</p>
          <div className="w-full bg-gray-300 rounded-full h-4 mb-3">
            <div className="h-4 rounded-full bg-blue-500" style={{ width: `${(hydration / hydrationGoal) * 100}%` }} />
          </div>
          <input type="number" value={hydration} onChange={(e) => setHydration(Number(e.target.value))} />
          <button onClick={() => updateMetric("hydration", hydration)}>Update</button>
        </div>

        {/* UV */}
        <div>
          <h2>UV Index</h2>
          <p>{uvIndex !== null ? uvIndex : "Loading..."}</p>
        </div>

        {/* Steps */}
        <div>
          <h2>Steps</h2>
          <p>{steps}</p>
          <input type="number" value={steps} onChange={(e) => setSteps(Number(e.target.value))} />
          <button onClick={() => updateMetric("steps", steps)}>Update</button>
        </div>

        {/* Mental Health */}
        <div>
          <h2>Mental Health</h2>
          <p>{mentalHealth}</p>
          <select value={mentalHealth} onChange={(e) => { setMentalHealth(e.target.value); updateMetric("mental_health", e.target.value); }}>
            <option>Good</option>
            <option>Okay</option>
            <option>Stressed</option>
            <option>Bad</option>
          </select>
        </div>

        {/* Air Quality */}
        <div>
          <h2>Air Quality</h2>
          <p>{airQuality !== null ? airQuality : "Enter ZIP code"}</p>
        </div>

        {/* Weather */}
        <div>
          <h2>Weather</h2>
          <p>{weather || "Enter ZIP code"}</p>
        </div>

        {/* Disasters */}
        <div>
          <h2>Recent Disasters</h2>
          {disasters.length ? (
            <ul>
              {disasters.map((d, idx) => (
                <li key={idx}>{d.title} ({d.categories.join(", ")})</li>
              ))}
            </ul>
          ) : (
            <p>Loading recent events...</p>
          )}
        </div>

        {/* Chatbot */}
        <div>
          <Chatbot apiEndpoint="/chatbot" />
        </div>
      </div>
    </div>
  );
}
