import React from "react";
import { Link, useLocation } from "react-router-dom";
import { useUI } from "../context/UIContext";

const Layout = ({ children }) => {
  const { theme, toggleTheme } = useUI();
  const location = useLocation();

  const navItems = [
    { name: "Home", path: "/" },
    { name: "About", path: "/about" },
    { name: "Dashboard", path: "/dashboard" },
    { name: "Settings", path: "/settings" },
  ];

  return (
    <div className={`${theme === "dark" ? "dark" : ""}`}>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white">
        <nav className="flex items-center justify-between px-8 py-4 shadow-md bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-white">
          <h1 className="text-2xl font-bold">SH Project</h1>
          <div className="flex gap-6 items-center">
            {navItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`font-medium hover:text-yellow-300 transition ${
                  location.pathname === item.path ? "underline" : ""
                }`}
              >
                {item.name}
              </Link>
            ))}
            <button
              onClick={toggleTheme}
              className="bg-white text-indigo-600 font-semibold px-3 py-1 rounded-lg hover:bg-indigo-200 transition"
            >
              {theme === "dark" ? "Light Mode" : "Dark Mode"}
            </button>
          </div>
        </nav>
        <main className="p-8">{children}</main>
      </div>
    </div>
  );
};

export default Layout;
